INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'PL', '*', '*', N'Po otrzymaniu pozycji objtych zamwieniem specjalnym klient jest zobowizany do niezwocznego starannego sprawdzenia otrzymanego towaru pod ktem zgodnoci z zamwieniem, kompletnoci i ewentualnych uszkodze. Wszelkie problemy naley zgosi w cigu piciu dni od daty otrzymania towaru. Sklep nie ponosi odpowiedzialnoci za rozwizanie problemw, ktre nie zostay zgoszone w cigu piciu dni od daty otrzymania. Potwierdzam, e zapoznaem(-am) si z powyszymi warunkami i wyraam na nie zgod.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
