INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_MERCH_TICKET_HEADER', 'PL', '*', '*', N'Należy umieścić tę metkę przy odpowiednim towarze, który umieszczono na przedpłacie.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_MERCH_TICKET_HEADER', 'PT', '*', '*', N'Coloque esse comprovante com a mercadoria correspondente que foi colocada na compra programada.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_MERCH_TICKET_HEADER', 'RU', '*', '*', N'Поместите этот билет с соответствующим товаром, который был зарезервирован.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_MERCH_TICKET_HEADER', 'SV', '*', '*', N'Placera den här etiketten tillsammans med motsvarande vara som har reserverats.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_MERCH_TICKET_HEADER', 'ZH', '*', '*', N'请将此票据与已放在预约订货中的相应商品放在一起。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'AR', '*', '*', N'أوافق على دفع الرصيد المستحق كما هو محدد في جدول الدفع. ولقد تسلمت نسخة من اتفاقية حجز البضاعة وبنودها، وأوافق على الالتزام بها.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'DE', '*', '*', N'Ich verpflichte mich, den fälligen Saldo gemäß dem Zeitplan zu bezahlen. Ich habe eine Kopie der Vereinbarung für Warenrücklagen erhalten und bin mit den darin enthaltenen Bedingungen einverstanden.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'ES', '*', '*', N'Acepto pagar el saldo adeudado conforme a lo descrito en el programa de pagos. He recibido una copia de las condiciones y términos del acuerdo de reservas y acepto cumplir con lo estipulado en los mismos.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'FR', '*', '*', N'Je m''engage à payer le solde dû conformément à l''échéancier de paiement. J''ai reçu une copie de la politique et des conditions de l''accord relatif aux mises de côté et je m''engage à les respecter.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'IT', '*', '*', N'Mi impegno a pagare il saldo dovuto secondo quanto stabilito nella pianificazione dei pagamenti. Ho ricevuto una copia dei termini e delle clausole dell''accordo per l''accantonamento e accetto di rispettarli.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'JA', '*', '*', N'支払スケジュールの記載に従って、負債残高を支払うことに同意します。取置契約の方針および条件のコピーを受領し、これに従うことに同意します。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'NL', '*', '*', N'Ik stem in met betaling van het verschuldigd saldo zoals vermeld in het betalingsschema. Ik heb een kopie van de voorwaarden van de Overeenkomst reservering ontvangen en ga akkoord met deze voorwaarden.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'PL', '*', '*', N'Wyrażam zgodę na opłacenie należnego salda zgodnie z harmonogramem płatności. Potwierdzam otrzymanie kopii warunków Umowy o przedpłatach i wyrażam na nie zgodę.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'PT', '*', '*', N'Eu concordo em pagar o saldo devido, conforme descrito na programação de pagamento. Recebi uma cópia da política e dos termos do Acordo de Compra Programada e concordo em cumpri-los.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'RU', '*', '*', N'Я выражаю согласие уплатить остаток задолженности в срок, указанный в следующем графике оплаты. Мной получена копия условий и правил соглашения о резервировании и я выражаю согласие их соблюдать.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'SV', '*', '*', N'Jag accepterar att betala hela beloppet i enlighet med betalningsplanen nedan. Jag har fått en kopia av policyn och villkoren för reservationsavtalet och förbinder mig att följa dem.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_LAYAWAY_TERMS', 'ZH', '*', '*', N'本人同意根据付款安排支付所欠余额。本人已收到“预约订货协议”政策和条款的副本，并同意严格遵照执行。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'AR', '*', '*', N'هل تريد هذا التلفاز الجديد، ولكنك لا ترغب في القيادة في جميع أنحاء المدينة بحثًا عنه؟ يمكننا تحديد موقعه لك في أي من متاجرنا ونعده للاستلام في المتجر الذي تختاره.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'DE', '*', '*', N'Suchen Sie nach einem neuen Fernsehgerät, möchten dafür aber nicht von Geschäft zu Geschäft fahren? Wir finden Ihr gewünschtes Gerät in einer unserer Filialen und stellen es Ihnen zur Abholung in der Filiale Ihrer Wahl bereit.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'ES', '*', '*', N'¿Quiere un nuevo televisor pero no desea recorrer la ciudad buscándolo? Podemos ocuparnos de buscarlo en nuestras tiendas y tenerlo preparado para que pase a retirarlo en la que usted elija.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'FR', '*', '*', N'Vous voulez acquérir cette nouvelle télévision sans avoir à courir dans toute la ville pour la trouver ? Nous pouvons rechercher pour vous dans quel magasin elle est disponible et la mettre à votre disposition pour retrait dans le magasin de votre choix.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'IT', '*', '*', N'Si desidera un apparecchio TV appena commercializzato, ma non si alcuna intenzione di attraversare la città in lungo e in largo per cercarlo? Noi possiamo trovarlo per conto del cliente in uno qualsiasi dei nostri negozi e prepararlo in modo che egli possa ritirarlo presso il negozio che preferisce.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'JA', '*', '*', N'新しいTVはほしいけど、それを探して町じゅうを車で走り回りたくはないですか? お客様のために私たちの店舗でそれを見つけて、お客様が選択した店舗でピッキングするように準備できます。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'NL', '*', '*', N'Wilt u die nieuwe tv, maar wilt u niet in de hele stad hoeven zoeken? Dan kunnen wij de tv in onze winkels zoeken en deze klaarzetten in uw winkel naar keuze om te worden opgehaald.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'PL', '*', '*', N'Chcesz nowy telewizor, ale nie masz ochoty szukać go po całym mieście? Znajdziemy dla Ciebie telewizor w jednym z naszych sklepów i przygotujemy go do odbioru w wybranym przez Ciebie sklepie.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'PT', '*', '*', N'Quer aquela TV nova, mas não quer dirigir pela cidade procurando por ela? Podemos localizá-la em qualquer uma de nossas lojas e prepará-la para retirada à sua escolha.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'RU', '*', '*', N'Хотите приобрести новый телевизор, но не хотите ездить по всему городу в его поисках? Мы можем найти его для вас в наших магазинах и доставить для самовывоза в выбранное место.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'SV', '*', '*', N'Vill du ha den där nya tv:n, men vill inte åka runt i stan och leta efter den? Vi kan leta reda på den i någon av våra butiker och ha den klar för hämtning i den butik du själv väljer.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_FOOTER', 'ZH', '*', '*', N'想要那款新电视，但是不想开车走遍全城去寻找？我们会为您在各家门店中寻找它，并您在所选的门店准备好它，供您提货。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'AR', '*', '*', N'شكرًا لاستخدامك لخدماتنا في احتياجات الطلبات الخاصة بك.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'DE', '*', '*', N'Vielen Dank für Ihren Auftrag.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'ES', '*', '*', N'Gracias por confiarnos sus pedidos.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'FR', '*', '*', N'Merci de passer par nous pour vos commandes.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'IT', '*', '*', N'Grazie per aver pensato a noi per qualsiasi esigenza relativa agli ordini.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'JA', '*', '*', N'オーダー・ニーズのすべてについて当店舗をご利用いただきありがとうございます。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'NL', '*', '*', N'Bedankt dat u uw order(s) bij ons hebt geplaatst.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'PL', '*', '*', N'Dziękujemy za skorzystanie z naszych usług we wszystkich kwestiach związanych z zamówieniem.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'PT', '*', '*', N'Obrigado por usar nossos serviços para todas as suas necessidades de pedidos.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'RU', '*', '*', N'Благодарим за обращение к нам по всем вопросам размещения заказа.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'SV', '*', '*', N'Tack för att du använder oss för dina beställningar.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_HEADER', 'ZH', '*', '*', N'感谢您选择我们满足所有订购需求。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'AR', '*', '*', N'يخضع استيفاء جميع الطلبات لتوفر السلع. نحن نفعل ما بوسعنا للتأكد من دقة المعلومات المتصلة بعملائنا ولكن توفر السلع في المتاجر شيء تقديري وليس مضمونًا.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'DE', '*', '*', N'Die Erfüllung sämtlicher Aufträge richtet sich nach der Warenverfügbarkeit. Wir bemühen uns, unseren Kunden möglichst genaue Informationen zur Verfügung zu stellen, jedoch ist die Verfügbarkeit der Waren in Filialen nur eine Schätzung und kann nicht garantiert werden.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'ES', '*', '*', N'La satisfacción de las órdenes está sujeta a la disponibilidad de la mercancía. Hacemos lo posible para asegurar la precisión de la información que se comunica a nuestros clientes, pero la disponibilidad de la mercancía en las tiendas es una estimación y no está garantizada.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'FR', '*', '*', N'L''exécution des commandes dépend de la disponibilité des marchandises. Nous faisons notre maximum pour communiquer à nos clients des informations aussi fiables que possible mais la disponibilité des marchandises dans les magasins est une estimation et ne peut pas être garantie.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'IT', '*', '*', N'L''evasione di tutti gli ordini è soggetta alla disponibilità della merce. Noi ci impegniamo al massimo per comunicare ai nostri clienti informazioni precise, ma la disponibilità della merce presso i vari negozi è esclusivamente stimata e non garantita.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'JA', '*', '*', N'すべてのオーダーのフルフィルメントは商品使用可能数の影響を受けます。顧客に連絡される情報の正確性を確実にするために最善を尽くしますが、店舗内の商品の使用可能数は見積であり、保証されません。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'NL', '*', '*', N'De afhandeling van alle orders is afhankelijk van de beschikbaarheid van de artikelen. Wij doen ons best u zo nauwkeurig mogelijk te informeren, maar we kunnen de beschikbaarheid in onze winkels alleen maar inschatten. We kunnen hiervoor geen garanties bieden.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'PL', '*', '*', N'Realizacja wszystkich zamówień zależy od dostępności towaru. Dokładamy wszelkich starań, aby informacje przekazywane klientom były jak najbardziej dokładne, jednak dostępność towarów w sklepach jest szacunkowa i nie jest gwarantowana.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'PT', '*', '*', N'O cumprimento de todos os pedidos está sujeito à disponibilidade da mercadoria. Fazemos o possível para garantir a precisão das informações comunicadas aos nossos clientes, mas a disponibilidade da mercadoria nas lojas é uma estimativa e não é garantida.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'RU', '*', '*', N'Выполнение заказов зависит от наличия товаров. Мы делаем все возможное, чтобы обеспечить точность информации, передаваемой нашим клиентам, но сведения о наличии товаров в магазинах являются ориентировочными, и наличие не гарантируется.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'SV', '*', '*', N'Expedieringen av ordern är beroende av varutillgängligheten. Vi gör vårt bästa för att informationen till våra kunder är så korrekt som möjligt, men varutillgängligheten i våra butiker än en uppskattning och kan inte garanteras.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_ORDER_TERMS', 'ZH', '*', '*', N'所有订单的履行都受商品可用性限制。我们尽力确保提供给客户的信息准确无误，但是门店中商品的可用性是预估的，不能完全保证。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'AR', '*', '*', N'إذا قام بائع التجزئة ببيع أي صنف أو نفدت كميته مؤقتًا فعندها سيعرض قسيمة شراء بنفس السعر لكل شخص يطلب توريد لذلك الصنف، بحيث يستطيع الشخص الشراء لاحقًا من بائع التجزئة منتجًا مثل ذلك الصنف وليس أكبر من سعر البيع للصنف في الوقت الذي تم فيه عرض قسيمة شراء بنفس السعر.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'DE', '*', '*', N'Wenn ein Artikel beim Einzelhändler ausverkauft oder vorübergehend nicht auf Lager ist, bietet der Einzelhändler jeder Person, die diesen Artikel angefordert hat, einen Gutschein mit Preisgarantie. Die Person kann dann später beim Einzelhändler ein vergleichbares Produkt zum selben Preis wie zum Zeitpunkt der Gutscheinausstellung erwerben.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'ES', '*', '*', N'Si el minorista ha agotado un artículo o se ha quedado temporalmente sin stock, ofrecerá a cada persona que solicite dicho artículo un vale para que pueda adquirir más adelante un producto similar a un precio que no será mayor que el precio de venta del artículo en el momento en el que se ofrece el vale.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'FR', '*', '*', N'Si le détaillant a vendu tous ses exemplaires de l''article demandé ou est en rupture de stock temporaire, il offrira à toutes les personnes qui en demanderont le réapprovisionnement un bon de réduction différé leur permettant d''acquérir le même article plus tard auprès du détaillant sans surcoût par rapport au prix d''achat du produit au moment où le bon est émis.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'IT', '*', '*', N'Qualora un articolo risultasse esaurito o temporaneamente non in magazzino, il retailer offrirà un buono articolo esaurito a chiunque richieda una fornitura dell''articolo in questione, in modo che la persona possa acquistare in un secondo momento, dal retailer, un prodotto equivalente a un prezzo di vendita non superiore a quello applicato per l''articolo nel momento in cui viene offerto il suddetto buono.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'JA', '*', '*', N'小売業者がアイテムを完売した場合、または一時的に欠品している場合、小売業者はそのアイテムの供給を要求する各個人に後日購入券を提供し、その個人がその小売業者からそのアイテムと同じ商品を後日購入券の提供時のアイテム販売価格以下で後で購入できるようにします。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'NL', '*', '*', N'Als het artikel bij de detailhandelaar is uitverkocht of tijdelijk niet op voorraad is, biedt de detailhandelaar iedereen die dat artikel aanvraagt een tegoedbon aan. Hetzelfde artikel kan dan later tegen dezelfde prijs bij de detailhandelaar worden gekocht.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'PL', '*', '*', N'Jeśli dana pozycja jest wyprzedana lub tymczasowo niedostępna u sprzedawcy, wówczas sprzedawca zaoferuje realizację danej pozycji w przyszłości każdej osobie składającej zamówienie na jej dostawę. Dzięki temu możliwy będzie zakup pozycji w późniejszym terminie od tego samego sprzedawcy, za cenę nie wyższą niż cena sprzedaży tej pozycji w momencie zaoferowania jej realizacji w przyszłości.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'PT', '*', '*', N'Se o varejista estiver esgotado ou temporariamente com falta de qualquer item em estoque, o varejista oferecerá um preço garantido a cada pessoa que solicitar o fornecimento desse item, para que a pessoa possa comprar mais tarde, do varejista, um produto igual ao item, não superior ao preço de venda do item no momento em que o preço garantido foi oferecido.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'RU', '*', '*', N'Если розничный продавец распродал какой-либо товар или он временно отсутствует на складе, он предложит каждому покупателю, который запрашивает поставку этого товара, гарантийный купон, чтобы покупатель мог позднее приобрести у этого розничного продавца такой же товар по цене, не превышающей цену продажи товара на момент предложения гарантийного купона.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'SV', '*', '*', N'Om detaljisten har sålt slut på en artikel, eller om en artikel är tillfälligt slut, kommer detaljisten att utfärda ett innestående erbjudande till varje person som begärt leverans av artikeln. Personen kan då vid ett senare tillfälle, hos detaljisten, köpa samma produkt till samma pris som artikeln hade när det innestående erbjudandet utfärdades.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_RAIN_CHECK_TERMS', 'ZH', '*', '*', N'如果零售商已经卖完商品或该商品临时缺货，则零售商会为申请该商品的每个人提供一张延期优惠券，允许其以后从零售商那里购买与该商品相同的产品，该同类商品在提供延期优惠券时的价格不高于该商品的销售价格。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'AR', '*', '*', N'غير صالح لإرجاع السلع. يخضع "الخصم بعد الشراء" للبنود والقوانين الإضافية في بعض الولايات.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'DE', '*', '*', N'Nicht gültig für Warenretouren. Rabatte unterliegen in einigen Bundesstaaten zusätzlichen Bedingungen und Vorschriften.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'ES', '*', '*', N'No es válido para devoluciones de mercancía. La devolución puede estar sujeta a condiciones y regulaciones adicionales en determinados estados.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'FR', '*', '*', N'Non valide pour un retour de marchandises. Les rabais sont susceptibles d''être régis par d''autres conditions et réglementations selon le lieu.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'IT', '*', '*', N'Non valido per un reso merce. Il rimborso può essere soggetto a ulteriori condizioni e normative in alcuni stati.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'JA', '*', '*', N'商品返品に有効ではありません。一部の都道府県では、リベートは追加条件および規制の対象になる場合があります。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'NL', '*', '*', N'Niet geldig voor het retourneren van handelswaar. Korting kan onderhevig zijn aan extra voorwaarden en voorschriften in bepaalde staten.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'PL', '*', '*', N'Nie obowiązuje w przypadku zwrotu towarów. Rabat może podlegać dodatkowym warunkom w niektórych stanach.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'PT', '*', '*', N'Não é válido para devolução de mercadoria. Os descontos podem estar sujeitos a termos e regulamentos adicionais em alguns estados.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'RU', '*', '*', N'Не распространяется на возврат товара. В некоторых штатах на ретро-бонусы могут распространяться дополнительные условия и правила.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'SV', '*', '*', N'Inte giltig för varuretur. Avdrag kan vara beroende av fler villkor och regler i vissa delstater.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_FOOTER', 'ZH', '*', '*', N'对商品退货无效。在某些省/州/市，返利可能需要遵守额外的条款和法规。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'AR', '*', '*', N'يجب أن يتم ختم طلبات الخصم بعد الشراء بختم البريد خلال 10 أيام من تاريخ الشراء الأصلي.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'DE', '*', '*', N'Rabatte müssen einen Poststempel innerhalb von 10 Tagen nach dem ursprünglichen Kaufdatum aufweisen.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'ES', '*', '*', N'Las devoluciones deben timbrarse en los 10 días siguientes a la fecha original de compra.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'FR', '*', '*', N'Les rabais doivent être validés par cachet de la poste dans les 10 jours suivant la date d''achat.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'IT', '*', '*', N'Il timbro deve essere apposto sui rimborsi entro 10 giorni dalla data di acquisto originaria.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'JA', '*', '*', N'リベートは、当初購入日から10日以内に消印が押される必要があります。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'NL', '*', '*', N'De poststempel van kortingen moet een datum binnen 10 dagen na de oorspronkelijke aankoop bevatten.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'PL', '*', '*', N'Rabaty muszą być wysłane pocztą w ciągu 10 dni od pierwotnej daty zakupu.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'PT', '*', '*', N'Os descontos devem ser marcados no prazo de 10 dias a contar da data original da compra.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'RU', '*', '*', N'Ретро-бонусы должны быть отправлены в течение 10 дней с даты покупки.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'SV', '*', '*', N'Avdrag måste poststämplas inom 10 dagar från det ursprungliga inköpsdatumet.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_REBATE_HEADER', 'ZH', '*', '*', N'返利必须在原始购买日期的 10 天内使用。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'AR', '*', '*', N'لا يمكن إرجاع السلعة إذا تم فتحها أو استخدامها من قبل.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'DE', '*', '*', N'Geöffnete oder verwendete Waren können nicht retourniert werden.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'ES', '*', '*', N'La mercancía no se puede devolver si se ha abierto o usado.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'FR', '*', '*', N'Les marchandises ne peuvent pas être retournées si elles ont été ouvertes ou utilisées.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'IT', '*', '*', N'La merce non può essere restituita se è stata aperta o utilizzata.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'JA', '*', '*', N'商品を開封または使用した場合、返品できません。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'NL', '*', '*', N'Handelwaar kan niet worden geretourneerd wanneer deze is geopend of gebruikt.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SALE_DISCLAIMER', 'PL', '*', '*', N'Towary otwarte lub używane nie podlegają zwrotom.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
