INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SEND_SALE_TERMS', 'PT', '*', '*', N'Todas as mercadorias serão entregues durante o período alocado. Aproximadamente vinte minutos antes de chegar, os motoristas entrarão em contato com você para informar quando, nesse período, sua mercadoria será entregue.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SEND_SALE_TERMS', 'RU', '*', '*', N'Все товары будут доставлены в течение выделенного периода времени. Водители свяжутся с вами примерно за двадцать минут до прибытия, чтобы сообщить, когда в течение этого периода будет доставлен ваш товар.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SEND_SALE_TERMS', 'SV', '*', '*', N'Alla varor levereras under den angivna tidsperioden. Chauffören kontaktar dig tjugo minuter före leverans för att tala om när din vara kommer att levereras.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SEND_SALE_TERMS', 'ZH', '*', '*', N'所有商品都将在分配的时间段交货。司机将在到达前大约二十分钟与您联系，让您了解您的商品将在什么时间交货。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
