INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_FOOTER', 'NL', '*', '*', N'De datum voor de beschikbaarheid van het artikel is slechts een schatting. De werkelijke datum kan afwijken.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
