INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_FOOTER', 'PT', '*', '*', N'A data de disponibilidade do item é fornecida apenas como uma estimativa. A data de disponibilidade real pode variar.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_FOOTER', 'RU', '*', '*', N'Дата доступности позиции указана как ориентировочная. Фактическая дата доступности может отличаться.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_FOOTER', 'SV', '*', '*', N'Artikelns tillgänglighetsdatum anges bara som en uppskattning. Den faktiska tillgängligheten kan variera.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_FOOTER', 'ZH', '*', '*', N'提供的商品可供货日期只是估计日期。实际可供货日期可能会发生变化。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'AR', '*', '*', N'يمكن استلام الأصناف الموجودة بالطلب الخاص أو إرسالها مباشرة إليك.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'DE', '*', '*', N'Artikel in einem Sonderauftrag können abgeholt oder direkt an Sie gesendet werden.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'ES', '*', '*', N'Los artículos incluidos en una orden especial pueden retirarse o se le pueden enviar directamente.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'FR', '*', '*', N'Les articles placés sur une commande spéciale pourront être mis à disposition pour retrait ou vous être expédiés directement.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'IT', '*', '*', N'Gli articoli inseriti in un ordine speciale possono essere ritirati o inviati direttamente al cliente.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'JA', '*', '*', N'特別オーダーに配置されたアイテムは、ピッキングまたは直接送付できます。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'NL', '*', '*', N'Artikelen die in een speciale order zijn opgenomen, kunnen direct worden opgehaald of naar u worden verzonden.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'PL', '*', '*', N'Pozycje objęte zamówieniem specjalnym można odebrać lub mogą one zostać wysłane bezpośrednio do klienta.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'PT', '*', '*', N'Os itens colocados no pedido especial podem ser retirados ou enviados diretamente a você.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'RU', '*', '*', N'Для позиций, размещенных в специальном заказе, возможен самовывоз или отправка по указанному адресу.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'SV', '*', '*', N'Artiklar i en specialorder kan hämtas eller skickas direkt till dig.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_HEADER', 'ZH', '*', '*', N'特殊订单中的商品可以提货，也可以直接发送给您。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'AR', '*', '*', N'عند استلام العميل للأصناف الموجودة بالطلب الخاص، يجب على العميل فحص السلعة فورًا وبحرص للتصحيح والاكتمال وللتحقق من أية تلفيات محتملة. يتم الإبلاغ عن أي مشكلة خلال خمسة أيام من استلام السلع. وأي مشكلة لم يتم الإبلاغ عنها خلال الخمسة أيام لن يكون المتجر مسئولاً عن تصحيحها. قرأت هذه البنود وأوافق على الالتزام بها.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'DE', '*', '*', N'Sobald der Kunde die Sonderauftragsartikel erhält, muss er die Waren umgehend und sorgfältig auf Korrektheit, Vollständigkeit und mögliche Schäden prüfen. Probleme sind innerhalb von fünf Tagen nach Erhalt der Ware zu melden. Die Filiale ist nicht für die Behebung von Problemen verantwortlich, die nicht innerhalb von fünf Tagen gemeldet werden. Ich habe diese Bedingungen gelesen und stimme ihnen zu.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'ES', '*', '*', N'Cuando el cliente recibe los artículos de la orden especial, debe inspeccionar detenidamente y en el momento la mercancía, para asegurarse de que es correcta, está completa y no tiene daños. Si hay algún problema, debe notificarse en un plazo de cinco días tras la recepción de la mercancía. La tienda no será responsable de corregir los problemas que no se hayan notificado en esos cinco días. He leído las condiciones y acepto cumplir con lo estipulado en ellas.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'FR', '*', '*', N'Lorsqu''il reçoit des articles d''une commande spéciale, le client doit vérifier immédiatement les marchandises réceptionnées et s''assurer que la livraison est correcte, complète et en bon état. Tout problème constaté doit être signalé dans un délai de cinq jours à compter de la réception des marchandises. Au-delà de ce délai, le problème ne pourra plus être considéré comme relevant de la responsabilité du magasin. J''ai pris connaissance des présentes conditions de vente et je les accepte.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'IT', '*', '*', N'Quando il cliente riceve gli articoli dell''ordine speciale, deve ispezionare immediatamente e con attenzione la merce per assicurarsi che sia corretta e completa e che non risulti danneggiata. Qualsiasi problema deve essere segnalato entro cinque giorni dal ricevimento della stessa. Se gli eventuali problemi non vengono segnalati entro cinque giorni, il negozio non sarà tenuto a risolverli. Ho letto queste condizioni e accetto di conformarmi ad esse.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'JA', '*', '*', N'顧客が特別オーダー・アイテムを受領するとき、顧客は、商品が正しいか、すべて揃っているか、および破損がないかをすぐに注意して調べる必要があります。問題がある場合は、商品の受領から5日以内に報告する必要があります。5日以内に報告されなかった問題は、店舗が是正する責任を負いません。この条件を読んで、これに従うことに同意します。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'NL', '*', '*', N'Wanneer de klant de artikelen van de speciale order ontvangt, moet deze direct en zorgvuldig controleren of de handelswaar juist en compleet is en geen gebreken vertoont. Alle problemen moeten binnen vijf dagen na ontvangst worden gemeld. Voor problemen die later worden gemeld, is de winkel niet verantwoordelijk. Ik heb deze voorwaarden gelezen en ga ermee akkoord.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'PL', '*', '*', N'Po otrzymaniu pozycji objętych zamówieniem specjalnym klient jest zobowiązany do niezwłocznego starannego sprawdzenia otrzymanego towaru pod kątem zgodności z zamówieniem, kompletności i ewentualnych uszkodzeń. Wszelkie problemy należy zgłosić w ciągu pięciu dni od daty otrzymania towaru. Sklep nie ponosi odpowiedzialności za rozwiązanie problemów, które nie zostały zgłoszone w ciągu pięciu dni od daty otrzymania. Potwierdzam, że zapoznałem(-am) się z powyższymi warunkami i wyrażam na nie zgodę.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'PT', '*', '*', N'Quando o cliente recebe os itens do pedido especial. O cliente deve inspecionar de maneira imediata e cuidadosa a mercadoria quanto à correção, integridade e possíveis danos. Quaisquer problemas devem ser relatados dentro de cinco dias após o recebimento da mercadoria. A loja não será responsável por corrigir quaisquer problemas não relatados dentro de cinco dias. Li estes termos e concordo em cumpri-los.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'RU', '*', '*', N'При получении позиций специального заказа клиент должен незамедлительно и тщательно осмотреть товар, чтобы убедиться в полном соответствии товара и его комплектации и отсутствии каких-либо повреждений. О любых проблемах необходимо сообщить в течение пяти дней с момента получения товара. Магазин не обязан исправлять недостатки, о которых не было сообщено в течение пяти дней. Мною прочитаны эти условия и я выражаю согласие их соблюдать.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'SV', '*', '*', N'När kunden tar emot specialorderartiklarna måste kunden omgående och noggrant inspektera varan för att se att det är rätt vara, att varan är komplett och att den är oskadad. Eventuella problem måste rapporteras inom fem dagar från det att kunden tog emot varan. Eventuella problem som inte rapporteras inom fem dagar är inte längre butikens ansvar att korrigera. Jag har läst dessa villkor och förbinder mig att följa dem.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_SPECIAL_ORDER_TERMS', 'ZH', '*', '*', N'客户接收特殊订单商品时，必须立即认真检验商品是否正确、完整以及是否存在任何可能的损坏。所有问题必须在收到商品的五天内报告。未在五天内报告的任何问题，门店将不负责更正。我已经阅读这些条款并同意遵守。', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_STORE_COPY_FOOTER', 'AR', '*', '*', N'احتفظ بإيصالات الاستلام في طلب المعاملة.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_STORE_COPY_FOOTER', 'DE', '*', '*', N'Bewahren Sie die Belege in der Reihenfolge der Transaktionen auf.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_STORE_COPY_FOOTER', 'ES', '*', '*', N'Conserve los recibos ordenados por transacción.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_STORE_COPY_FOOTER', 'FR', '*', '*', N'Conserver les reçus dans l''ordre des transactions.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
INSERT INTO com_translations (organization_id, translation_key, locale, org_code, org_value, translation, create_date, create_user_id, update_date, update_user_id) values ($(OrgID), '+RECEIPT_TEXT_STORE_COPY_FOOTER', 'IT', '*', '*', N'Conservare le ricevute in base all''ordine delle transazioni.', GETUTCDATE(), 'BASEDATA', GETUTCDATE(), 'BASEDATA');
